import { Component, OnInit, Inject } from '@angular/core';
import { ViewChild } from '@angular/core';
import { ApiService } from '../services/api.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA} from "@angular/material/dialog";
import { MatPaginator } from '@angular/material/paginator';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-dialog',
  templateUrl: './dialog.component.html',
  styleUrls: ['./dialog.component.css']
})
export class DialogComponent implements OnInit {
  userForm !: FormGroup;

  dataSource!: MatTableDataSource<any>;

  @ViewChild(MatPaginator) paginator!: MatPaginator;
  @ViewChild(MatSort) sort!: MatSort

  constructor(private api : ApiService, private formBuilder: FormBuilder, 
    @Inject(MAT_DIALOG_DATA) public editData : any) {}

  ngOnInit(): void {
    this.userForm = this.formBuilder.group({
      user : ["", Validators.required],
      email : ["", Validators.required],
      mobile : ["", Validators.required],
    })
    if(this.editData){
      this.userForm.controls["user"].setValue(this.editData.user)
      this.userForm.controls["email"].setValue(this.editData.email)
      this.userForm.controls["mobile"].setValue(this.editData.mobile)
    }
  }
  updateUser(){
    this.api
  }

}
<main class="addUser">
    <h1>Edit user</h1>
    <form class="example-form" [formGroup] = "userForm">
        <mat-form-field class="example-full-width">
          <mat-label>User</mat-label>
          <input formControlName="user" type="text" matInput placeholder="Add the user">
        </mat-form-field>
        <mat-form-field class="example-full-width">
          <mat-label>Email</mat-label>
          <input formControlName="email" type="email" matInput placeholder="Add the user`s e-mail">
        </mat-form-field>
        <mat-form-field class="example-full-width">
          <mat-label>Mobile number</mat-label>
          <input formControlName="mobile" type="tel" matInput placeholder="Add the user`s mobile number">
        </mat-form-field>
        <button class="addUser-btn" mat-flat-button color="primary">Edit user</button>
    </form>
  </main>
